import React, {useState} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {Link} from 'react-router-dom';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import HomeIcon from '@material-ui/icons/Home';
import AddBoxIcon from '@material-ui/icons/AddBox';
import LoginIcon from '@mui/icons-material/Login';
import LogoutIcon from '@mui/icons-material/Logout';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

function Header() {
    const dispatch = useDispatch();
    const userStatus = useSelector(store => store.user);
    const [open, setOpen] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleClose = () => {
        setOpen(false);
    };


    const clearDetailsReducer = () => {
        dispatch({
            type: 'CLEAR_DETAILS'
        })
    }

    const openLoginModal = () => {
        setOpen(true);
        // dispatch({
        //     type: 'LOGIN_USER'
        // })
    };

    const login = () => {
      console.log(password, username)
    };

    const logout = () => {
        dispatch({
            type: 'LOGOUT_USER'
        })
    };

    return (
        <>
            <Dialog open={open} onClose={handleClose}>
                <DialogTitle>Login</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        Please Login
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="username"
                        label="Username"
                        type="text"
                        fullWidth
                        variant="standard"
                        value={username}
                        onChange={({target: {value}}) => setUsername(value)}
                    />

                    <TextField
                        margin="dense"
                        id="password"
                        label="Password"
                        type="password"
                        fullWidth
                        variant="standard"
                        value={password}
                        onChange={({target: {value}}) => setPassword(value)}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose}>Cancel</Button>
                    <Button onClick={login}>Login</Button>
                </DialogActions>
            </Dialog>

            <div id="header-container">
                <h1 id="app-title">The Movies</h1>

                <Box m={1}>
                    <Button component={Link} to="/" onClick={clearDetailsReducer}>
                        <HomeIcon/>Home
                    </Button>

                    {
                        userStatus ?
                            <>
                                <Button component={Link} to="/addmovie">
                                    <AddBoxIcon/>Add Movie
                                </Button>

                                <Button onClick={logout} to="/addmovie">
                                    <LoginIcon/>Log Out
                                </Button>
                            </>
                            :
                            <Button onClick={openLoginModal} to="/addmovie">
                                <LogoutIcon/>Log In
                            </Button>
                    }
                </Box>
            </div>
        </>
    )
}

export default Header;
