import React from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {useHistory, Link} from 'react-router-dom';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import HomeIcon from '@material-ui/icons/Home';
import AddBoxIcon from '@material-ui/icons/AddBox';
import LoginIcon from '@mui/icons-material/Login';
import LogoutIcon from '@mui/icons-material/Logout';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};

function Header() {
    const history = useHistory();
    const dispatch = useDispatch();
    const userStatus = useSelector(store => store.user);

    const clearDetailsReducer = () => {
        dispatch({
            type: 'CLEAR_DETAILS'
        })
    }

    const login = () => {
        dispatch({
            type: 'LOGIN_USER'
        })
    };

    const logout = () => {
        dispatch({
            type: 'LOGOUT_USER'
        })
    };

    return (
        <>
            <Dialog open={true} onClose={handleClose}>
                <DialogTitle>Subscribe</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        To subscribe to this website, please enter your email address here. We
                        will send updates occasionally.
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="name"
                        label="Email Address"
                        type="email"
                        fullWidth
                        variant="standard"
                    />
                </DialogContent>
                <DialogActions>
                    {/*<Button onClick={handleClose}>Cancel</Button>*/}
                    {/*<Button onClick={handleClose}>Subscribe</Button>*/}
                </DialogActions>
            </Dialog>

            <div id="header-container">
                <h1 id="app-title">The Movies</h1>

                <Box m={1}>
                    <Button component={Link} to="/" onClick={clearDetailsReducer}>
                        <HomeIcon/>Home
                    </Button>

                    {
                        userStatus ?
                            <>
                                <Button component={Link} to="/addmovie">
                                    <AddBoxIcon/>Add Movie
                                </Button>

                                <Button onClick={logout} to="/addmovie">
                                    <LoginIcon/>Log Out
                                </Button>
                            </>
                            :
                            <Button onClick={login} to="/addmovie">
                                <LogoutIcon/>Log In
                            </Button>
                    }
                </Box>
            </div>
        </>
    )
}

export default Header;
