import React from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {useHistory, Link} from 'react-router-dom';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import HomeIcon from '@material-ui/icons/Home';
import AddBoxIcon from '@material-ui/icons/AddBox';
import LoginIcon from '@mui/icons-material/Login';
import LogoutIcon from '@mui/icons-material/Logout';


function Header() {
    const history = useHistory();
    const dispatch = useDispatch();
    const userStatus = useSelector(store => store.user);
    // // local state to control nav button colors
    // const [homeColor, setHomeColor] = useState("default");
    // const [addColor, setAddColor] = useState("default");

    // let { id } = useParams();
    // if(id === "addmovie") {

    //   setAddColor("primary");
    // }

    // console.log('id', id);

    // const homeClick = () => {
    //   setHomeColor("primary");
    //   setAddColor("default");
    // }

    // const addClick = () => {
    //   setHomeColor("default");
    //   setAddColor("primary");
    // }

    // on 'back to to list' click,
    // clear the details reducer
    // so that the next movie clicked doesn't load weird
    const clearDetailsReducer = () => {
        dispatch({
            type: 'CLEAR_DETAILS'
        })
    }

    const login = () => {
        dispatch({
            type: 'LOGIN_USER'
        })
    };

    const logout = () => {
        dispatch({
            type: 'LOGOUT_USER'
        })
    };

    return (
        <div id="header-container">
            <h1 id="app-title">The Movies</h1>

            <Box m={1}>
                <Button component={Link} to="/" onClick={clearDetailsReducer}>
                    <HomeIcon/>Home
                </Button>

                {
                    userStatus ?
                        <>
                            <Button component={Link} to="/addmovie">
                                <AddBoxIcon/>Add Movie
                            </Button>

                            <Button onClick={logout} to="/addmovie">
                                <LoginIcon/>Log Out
                            </Button>
                        </>
                        :
                        <Button onClick={login} to="/addmovie">
                            <LogoutIcon/>Log In
                        </Button>
                }
            </Box>
        </div>
    )
}

export default Header;
