import React from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {useHistory, Link} from 'react-router-dom';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import HomeIcon from '@material-ui/icons/Home';
import AddBoxIcon from '@material-ui/icons/AddBox';
import LoginIcon from '@mui/icons-material/Login';
import LogoutIcon from '@mui/icons-material/Logout';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';

const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};

function Header() {
    const history = useHistory();
    const dispatch = useDispatch();
    const userStatus = useSelector(store => store.user);

    const clearDetailsReducer = () => {
        dispatch({
            type: 'CLEAR_DETAILS'
        })
    }

    const login = () => {
        dispatch({
            type: 'LOGIN_USER'
        })
    };

    const logout = () => {
        dispatch({
            type: 'LOGOUT_USER'
        })
    };

    return (
        <>
            <Modal
                open={true}
                // onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <Typography id="modal-modal-title" variant="h6" component="h2">
                        Text in a modal
                    </Typography>
                    <Typography id="modal-modal-description" sx={{mt: 2}}>
                        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                    </Typography>
                </Box>
            </Modal>

            <div id="header-container">
                <h1 id="app-title">The Movies</h1>

                <Box m={1}>
                    <Button component={Link} to="/" onClick={clearDetailsReducer}>
                        <HomeIcon/>Home
                    </Button>

                    {
                        userStatus ?
                            <>
                                <Button component={Link} to="/addmovie">
                                    <AddBoxIcon/>Add Movie
                                </Button>

                                <Button onClick={logout} to="/addmovie">
                                    <LoginIcon/>Log Out
                                </Button>
                            </>
                            :
                            <Button onClick={login} to="/addmovie">
                                <LogoutIcon/>Log In
                            </Button>
                    }
                </Box>
            </div>
        </>
    )
}

export default Header;
